import apiClient from './apiClient';

// Get all orders for current user
export const getUserOrders = async () => {
  const response = await apiClient.get('/orders');
  return response.json();
};

// Get order by ID
export const getOrderById = async (orderId) => {
  const response = await apiClient.get(`/orders/${orderId}`);
  return response.json();
};

// Create new order
export const createOrder = async (orderData) => {
  const response = await apiClient.post('/orders', orderData);
  return response.json();
};

// Update order
export const updateOrder = async (orderId, orderData) => {
  const response = await apiClient.put(`/orders/${orderId}`, orderData);
  return response.json();
};

// Delete order
export const deleteOrder = async (orderId) => {
  const response = await apiClient.delete(`/orders/${orderId}`);
  return response.json();
};

// Join existing order
export const joinOrder = async (orderId) => {
  const response = await apiClient.post(`/orders/${orderId}/join`);
  return response.json();
};

// Leave order
export const leaveOrder = async (orderId) => {
  const response = await apiClient.post(`/orders/${orderId}/leave`);
  return response.json();
};
