import apiClient from './apiClient';

// Get delivery status for an order
export const getDeliveryStatus = async (orderId) => {
  const response = await apiClient.get(`/delivery/${orderId}`);
  return response.json();
};

// Update delivery status
export const updateDeliveryStatus = async (orderId, statusData) => {
  const response = await apiClient.put(`/delivery/${orderId}`, statusData);
  return response.json();
};

// Track delivery
export const trackDelivery = async (orderId) => {
  const response = await apiClient.get(`/delivery/${orderId}/track`);
  return response.json();
};
