import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { tamarindAPI } from '../api/tamarindAPI';
import FoodItemCard from './shared/FoodItemCard';

const TamarindMenuPage = ({ setCurrentPage, setSelectedFood, setSelectedRestaurant }) => {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [menuData, setMenuData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMenuData = async () => {
      try {
        setLoading(true);
        const data = await tamarindAPI.getFullMenu();
        setMenuData(data);
      } catch (error) {
        console.error('Error fetching menu:', error);
        setError('Failed to load menu. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchMenuData();
  }, []);

  // Get all menu items for filtering
  const allMenuItems = menuData ? (menuData.menu || []) : [];

  // Filter menu items based on search and category
  const filteredMenuItems = allMenuItems.filter(item => {
    if (!item || !item.name || !item.description) {
      return false;
    }
    const matchesSearch = item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         item.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || item.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const handleFoodItemClick = (food) => {
    setSelectedFood(food);
    setSelectedRestaurant(menuData.restaurant);
    setCurrentPage('food-sharing');
  };

  // Get unique categories from menu items
  const categories = menuData ? ['all', ...new Set(allMenuItems.filter(item => item && item.category).map(item => item.category))] : [];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-xl text-blue-400">Catching Tamarind's fresh seafood menu...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <svg className="w-8 h-8 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
            </svg>
          </div>
          <h2 className="text-2xl font-bold text-blue-400 mb-2">Oops! Something went wrong</h2>
          <p className="text-blue-400 mb-4">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 transition-colors duration-300"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  if (!menuData) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <p className="text-blue-400">No menu data available.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900">
      {/* Header */}
      <div className="bg-gray-800 border-b border-gray-700">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setCurrentPage('restaurants')}
                className="text-blue-400 hover:text-blue-300 transition-colors duration-300 flex items-center space-x-2"
              >
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
                <span>Back to Restaurants</span>
              </button>
              
              <div className="text-center">
                <div className="flex items-center justify-center space-x-2 mb-1">
                  <svg className="w-5 h-5 text-blue-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                  </svg>
                  <h1 className="text-3xl font-bold text-white">{menuData.restaurant.name}</h1>
                </div>
                <p className="text-gray-300">{menuData.restaurant.location}</p>
              </div>
              
              <div className="text-right">
                <div className="flex items-center space-x-2 text-white">
                  <span className="text-yellow-400">â˜…</span>
                  <span>4.7 (1,500)</span>
                </div>
                <p className="text-gray-300">$$$</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Seafood Header */}
      <div className="bg-gray-700 border-b border-gray-600">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="text-center">
            <h3 className="text-lg font-semibold text-white mb-2">ğŸŸ Fresh Ocean Seafood</h3>
            <p className="text-gray-300 text-sm">Daily catch from the Indian Ocean, prepared with premium ingredients and ocean views!</p>
          </div>
        </div>
      </div>

      {/* Search and Filters */}
      <div className="bg-gray-800 border-b border-gray-700">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex flex-col md:flex-row gap-4">
            {/* Search Bar */}
            <div className="flex-1">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search Tamarind menu..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full px-4 py-3 pl-12 bg-gray-700 border-0 rounded-lg focus:ring-2 focus:ring-blue-500 focus:bg-gray-600 transition-all duration-300 text-white placeholder-gray-400"
                />
                <svg className="w-5 h-5 text-gray-400 absolute left-4 top-1/2 transform -translate-y-1/2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
            </div>

            {/* Category Filter */}
            <div className="flex space-x-2 overflow-x-auto">
              {categories.map((category) => (
                <button
                  key={category}
                  onClick={() => setSelectedCategory(category)}
                  className={`px-4 py-2 rounded-lg font-medium whitespace-nowrap transition-colors duration-300 ${
                    selectedCategory === category
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                  }`}
                >
                  {category === 'all' ? 'All Items' : category}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Menu Items */}
      <div className="max-w-7xl mx-auto px-4 py-6">
        {filteredMenuItems.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-400 text-lg">No menu items found matching your search.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredMenuItems.map((item, index) => (
              <FoodItemCard
                key={item.id || index}
                item={item}
                index={index}
                onClick={handleFoodItemClick}
                theme="dark"
                accentColor="blue"
              />
            ))}
          </div>
        )}
      </div>

      {/* Restaurant Info Footer */}
      <div className="bg-gray-800 border-t border-gray-700 mt-12">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <div className="text-center">
            <h3 className="text-lg font-semibold text-blue-400 mb-2">About {menuData.restaurant.name}</h3>
            <p className="text-gray-300 mb-4">
              {menuData.restaurant.description || 'Kenya\'s premier seafood restaurant with locations in Mombasa and Nairobi since 1980'}
            </p>
            
            {/* Restaurant stats */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-400">1980</div>
                <div className="text-sm text-gray-400">Founded</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-400">2</div>
                <div className="text-sm text-gray-400">Branches</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-400">{menuData.restaurant.rating}</div>
                <div className="text-sm text-gray-400">Rating</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-400">45-60</div>
                <div className="text-sm text-gray-400">Delivery (min)</div>
              </div>
            </div>

            {/* Specialties */}
            {menuData.restaurant.specialties && Array.isArray(menuData.restaurant.specialties) && menuData.restaurant.specialties.length > 0 && (
              <div className="mb-6">
                <h4 className="text-sm font-semibold text-blue-400 mb-2">Our Specialties:</h4>
                <div className="flex flex-wrap justify-center gap-2">
                  {menuData.restaurant.specialties.map((specialty, index) => (
                    <span key={index} className="bg-gray-700 text-blue-400 px-3 py-1 rounded-full text-xs">
                      {specialty}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Contact and location info */}
            <div className="flex justify-center space-x-6 text-sm text-gray-400">
              <span>ğŸ“ {menuData.restaurant.location}</span>
              <span>ğŸ“ {menuData.restaurant.phone}</span>
              <span>ğŸŒ <a href={menuData.restaurant.website} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline">Website</a></span>
            </div>

            {/* Popular locations */}
            {menuData.locations && Array.isArray(menuData.locations) && menuData.locations.length > 0 && (
              <div className="mt-6">
                <h4 className="text-sm font-semibold text-blue-700 mb-2">Our Locations:</h4>
                <div className="flex flex-wrap justify-center gap-2">
                  {menuData.locations.map((location, index) => (
                    <span key={index} className="bg-blue-100 text-blue-600 px-3 py-1 rounded-full text-xs">
                      {location}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Deals Info */}
            {menuData.deals && Array.isArray(menuData.deals) && menuData.deals.length > 0 && (
              <div className="mt-6">
                <h4 className="text-sm font-semibold text-blue-700 mb-2">Current Deals:</h4>
                <div className="flex flex-wrap justify-center gap-2">
                  {menuData.deals.map((deal, index) => (
                    <span key={index} className="bg-blue-100 text-blue-600 px-3 py-1 rounded-full text-xs">
                      ğŸ”¥ {deal}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Rewards Info */}
            {menuData.rewards && menuData.rewards.available && menuData.rewards.benefits && Array.isArray(menuData.rewards.benefits) && (
              <div className="mt-6">
                <h4 className="text-sm font-semibold text-blue-700 mb-2">{menuData.rewards.program} Benefits:</h4>
                <div className="flex flex-wrap justify-center gap-2">
                  {menuData.rewards.benefits.map((benefit, index) => (
                    <span key={index} className="bg-blue-100 text-blue-600 px-3 py-1 rounded-full text-xs">
                      ğŸ {benefit}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Customization Options */}
            {menuData.customization && menuData.customization.available && (
              <div className="mt-6">
                <h4 className="text-sm font-semibold text-blue-700 mb-2">Customization Options:</h4>
                <div className="space-y-3">
                  {menuData.customization.options.doneness && (
                    <div>
                      <h5 className="text-xs font-medium text-blue-600 mb-1">Doneness Options:</h5>
                      <div className="flex flex-wrap justify-center gap-1">
                        {menuData.customization.options.doneness.map((option, index) => (
                          <span key={index} className="bg-blue-100 text-blue-600 px-2 py-1 rounded text-xs">
                            ğŸ”¥ {option}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                  {menuData.customization.options.fishTypes && (
                    <div>
                      <h5 className="text-xs font-medium text-blue-600 mb-1">Fish Types:</h5>
                      <div className="flex flex-wrap justify-center gap-1">
                        {menuData.customization.options.fishTypes.map((option, index) => (
                          <span key={index} className="bg-cyan-100 text-cyan-600 px-2 py-1 rounded text-xs">
                            ğŸŸ {option}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                  {menuData.customization.options.sauces && (
                    <div>
                      <h5 className="text-xs font-medium text-blue-600 mb-1">Sauce Options:</h5>
                      <div className="flex flex-wrap justify-center gap-1">
                        {menuData.customization.options.sauces.map((option, index) => (
                          <span key={index} className="bg-yellow-100 text-yellow-600 px-2 py-1 rounded text-xs">
                            ğŸ¥« {option}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TamarindMenuPage;
