import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { fetchCurrentUser } from '../api';

const UserProfilePage = ({ setCurrentPage }) => {
  const [userData, setUserData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    postalCode: ''
  });
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [message, setMessage] = useState('');

  // Fetch current user data
  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      setIsLoading(true);
      
      // First try to get user data from localStorage (from signup)
      const localUser = JSON.parse(localStorage.getItem('user') || '{}');
      
      if (localUser.firstName || localUser.lastName || localUser.email) {
        // Use the data from signup
        setUserData({
          firstName: localUser.firstName || '',
          lastName: localUser.lastName || '',
          email: localUser.email || '',
          phone: localUser.phone || '',
          address: localUser.address || '',
          city: localUser.city || '',
          postalCode: localUser.postalCode || ''
        });
      } else {
        // Fallback to backend API if no local data
        const data = await fetchCurrentUser();
        
        if (data.user) {
          setUserData({
            firstName: data.user.firstName || data.user.name?.split(' ')[0] || '',
            lastName: data.user.lastName || data.user.name?.split(' ')[1] || '',
            email: data.user.email || '',
            phone: data.user.phone || '',
            address: data.user.address || '',
            city: data.user.city || '',
            postalCode: data.user.postalCode || ''
          });
        }
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
      setMessage('Failed to load profile data. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleEditProfile = () => {
    setCurrentPage('complete-profile');
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-red-50 flex items-center justify-center">
        <motion.div
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          className="text-center"
        >
          <div className="w-16 h-16 border-4 border-orange-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600 text-lg">Loading your profile...</p>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-red-50 relative overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute inset-0" style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ff6b35' fill-opacity='0.4'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
        }}></div>
      </div>

      <div className="relative z-10 p-4">
        {/* Back Button */}
        <motion.button
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.6 }}
          onClick={() => setCurrentPage('home')}
          className="absolute top-8 left-8 text-gray-600 hover:text-orange-600 transition-colors duration-300 flex items-center space-x-2 backdrop-blur-sm bg-white/30 rounded-lg px-3 py-2"
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
          <span>Back to Home</span>
        </motion.button>

        <div className="max-w-4xl mx-auto pt-20">
          {/* Profile Header */}
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center mb-12"
          >
            <div className="w-32 h-32 bg-gradient-to-br from-orange-400 to-red-500 rounded-full flex items-center justify-center mx-auto mb-6 shadow-2xl">
              <span className="text-6xl text-white font-bold">
                {userData.firstName ? userData.firstName.charAt(0).toUpperCase() : 'U'}
              </span>
            </div>
            <h1 className="text-5xl font-bold bg-gradient-to-r from-orange-600 to-red-600 bg-clip-text text-transparent mb-4">
              {userData.firstName && userData.lastName 
                ? `${userData.firstName} ${userData.lastName}` 
                : 'Your Profile'
              }
            </h1>
            <p className="text-xl text-gray-600">Welcome to your Gawa profile</p>
          </motion.div>

          {/* Message */}
          {message && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              className="mb-8 p-4 bg-red-100 text-red-700 rounded-lg text-center max-w-md mx-auto"
            >
              {message}
            </motion.div>
          )}

          {/* Profile Information Grid */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-12"
          >
            {/* Personal Information */}
            <div className="bg-white rounded-2xl shadow-2xl p-8">
              <div className="flex items-center mb-6">
                <div className="w-12 h-12 bg-gradient-to-br from-blue-400 to-blue-600 rounded-full flex items-center justify-center mr-4">
                  <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                  </svg>
                </div>
                <h2 className="text-2xl font-bold text-gray-800">Personal Information</h2>
              </div>
              
              <div className="space-y-4">
                <div className="flex items-center p-4 bg-gray-50 rounded-lg">
                  <span className="text-gray-500 font-medium w-24">Name:</span>
                  <span className="text-gray-800 font-semibold">
                    {userData.firstName && userData.lastName 
                      ? `${userData.firstName} ${userData.lastName}` 
                      : 'Not provided'
                    }
                  </span>
                </div>
                
                <div className="flex items-center p-4 bg-gray-50 rounded-lg">
                  <span className="text-gray-500 font-medium w-24">Email:</span>
                  <span className="text-gray-800 font-semibold">
                    {userData.email || 'Not provided'}
                  </span>
                </div>
                
                <div className="flex items-center p-4 bg-gray-50 rounded-lg">
                  <span className="text-gray-500 font-medium w-24">Phone:</span>
                  <span className="text-gray-800 font-semibold">
                    {userData.phone || 'Not provided'}
                  </span>
                </div>
              </div>
            </div>

            {/* Address Information */}
            <div className="bg-white rounded-2xl shadow-2xl p-8">
              <div className="flex items-center mb-6">
                <div className="w-12 h-12 bg-gradient-to-br from-green-400 to-green-600 rounded-full flex items-center justify-center mr-4">
                  <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                  </svg>
                </div>
                <h2 className="text-2xl font-bold text-gray-800">Address Information</h2>
              </div>
              
              <div className="space-y-4">
                <div className="flex items-center p-4 bg-gray-50 rounded-lg">
                  <span className="text-gray-500 font-medium w-24">Address:</span>
                  <span className="text-gray-800 font-semibold">
                    {userData.address || 'Not provided'}
                  </span>
                </div>
                
                <div className="flex items-center p-4 bg-gray-50 rounded-lg">
                  <span className="text-gray-500 font-medium w-24">City:</span>
                  <span className="text-gray-800 font-semibold">
                    {userData.city || 'Not provided'}
                  </span>
                </div>
                
                <div className="flex items-center p-4 bg-gray-50 rounded-lg">
                  <span className="text-gray-500 font-medium w-24">Postal Code:</span>
                  <span className="text-gray-800 font-semibold">
                    {userData.postalCode || 'Not provided'}
                  </span>
                </div>
              </div>
            </div>
          </motion.div>

          {/* Action Buttons */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            className="text-center space-y-4"
          >
            <button
              onClick={handleEditProfile}
              className="bg-gradient-to-r from-orange-500 to-red-500 text-white py-4 px-8 rounded-xl font-semibold text-lg hover:from-orange-600 hover:to-red-600 transition-all duration-300 transform hover:scale-105 shadow-xl"
            >
              Edit Profile
            </button>
            
            <div className="text-gray-500">
              <p>Need to update your information? Click the button above to edit your profile.</p>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default UserProfilePage;
