import React from 'react';
import { motion } from 'framer-motion';
import './App.css';

// Navigation Component
const Navigation = () => (
  <nav className="fixed top-0 w-full bg-gray-900/90 backdrop-blur-md shadow-sm z-50">
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      <div className="flex justify-between items-center py-4">
        <div className="flex items-center">
          <h1 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-500"> Gawa</h1>
        </div>
        <div className="hidden md:flex space-x-8">
          <a href="#home" className="text-gray-300 hover:text-cyan-400 transition-all duration-300 font-semibold hover:scale-105 transform relative group">
            Home
            <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-to-r from-cyan-400 to-blue-500 transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a href="#how-it-works" className="text-gray-300 hover:text-cyan-400 transition-all duration-300 font-semibold hover:scale-105 transform relative group">
            How it Works
            <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-to-r from-cyan-400 to-blue-500 transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a href="#join-order" className="text-gray-300 hover:text-cyan-400 transition-all duration-300 font-semibold hover:scale-105 transform relative group">
            Join Order
            <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-to-r from-cyan-400 to-blue-500 transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a href="#contact" className="text-gray-300 hover:text-cyan-400 transition-all duration-300 font-semibold hover:scale-105 transform relative group">
            Contact
            <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-to-r from-cyan-400 to-blue-500 transition-all duration-300 group-hover:w-full"></span>
          </a>
        </div>
        <button className="md:hidden text-gray-300">
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        </button>
      </div>
    </div>
  </nav>
);

// Hero Section Component
const HeroSection = () => (
  <section id="home" className="relative pt-20 pb-16 min-h-screen flex items-center overflow-hidden">
    {/* Animated Background Pattern */}
    <div className="absolute inset-0 bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900">
      <div className="absolute inset-0 opacity-30 bg-[url('data:image/svg+xml,%3Csvg%20width%3D%2260%22%20height%3D%2260%22%20viewBox%3D%220%200%2060%2060%22%20xmlns%3D%22http%3A//www.w3.org/2000/svg%22%3E%3Cg%20fill%3D%22none%22%20fill-rule%3D%22evenodd%22%3E%3Cg%20fill%3D%22%23ffffff%22%20fill-opacity%3D%220.05%22%3E%3Ccircle%20cx%3D%2230%22%20cy%3D%2230%22%20r%3D%222%22/%3E%3C/g%3E%3C/g%3E%3C/svg%3E')]"></div>
    </div>

    {/* Floating Food Icons */}
    <motion.div 
      animate={{ 
        y: [0, -20, 0],
        rotate: [0, 5, 0]
      }}
      transition={{ 
        duration: 6, 
        repeat: Infinity, 
        ease: "easeInOut" 
      }}
      className="absolute top-20 right-20 text-6xl opacity-20"
    >

    </motion.div>
    <motion.div 
      animate={{ 
        y: [0, 15, 0],
        rotate: [0, -5, 0]
      }}
      transition={{ 
        duration: 8, 
        repeat: Infinity, 
        ease: "easeInOut",
        delay: 1
      }}
      className="absolute top-40 right-40 text-5xl opacity-20"
    >

    </motion.div>
    <motion.div 
      animate={{ 
        y: [0, -10, 0],
        rotate: [0, 3, 0]
      }}
      transition={{ 
        duration: 7, 
        repeat: Infinity, 
        ease: "easeInOut",
        delay: 2
      }}
      className="absolute top-60 right-60 text-4xl opacity-20"
    >

    </motion.div>

    {/* Content */}
    <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 w-full">
      <div className="grid lg:grid-cols-2 gap-12 items-center">
        {/* Left Content */}
        <motion.div 
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="space-y-8"
        >
          <div className="inline-flex items-center px-4 py-2 bg-white/10 backdrop-blur-sm rounded-full border border-white/20 mb-6">
            <span className="w-2 h-2 bg-green-400 rounded-full mr-3 animate-pulse"></span>
            <span className="text-white/90 text-sm font-medium">Live in 50+ cities</span>
          </div>

          <h1 className="text-5xl lg:text-7xl font-black text-white leading-tight tracking-tight">
            Split Orders,{' '}
            <span className="text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 via-blue-400 to-purple-400">
              Share Savings
            </span>
          </h1>

          <p className="text-xl lg:text-2xl text-blue-100 leading-relaxed font-medium">
            Join group orders and pay only for your portion. No more buying whole pizzas when you only want a few slices! 
            Connect with neighbors, coworkers, and friends to enjoy great food at better prices.
          </p>

          <div className="flex flex-col sm:flex-row gap-4">
            <motion.button 
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
              className="group bg-gradient-to-r from-cyan-500 to-blue-600 text-white px-8 py-4 rounded-2xl text-lg font-bold hover:from-cyan-600 hover:to-blue-700 transition-all duration-300 shadow-lg hover:shadow-2xl hover:shadow-cyan-500/25"
            >
              <span className="flex items-center">
                <span>Join Order Now</span>
                <motion.span 
                  animate={{ x: [0, 5, 0] }}
                  transition={{ duration: 1.5, repeat: Infinity }}
                  className="ml-2 text-xl"
                >

                </motion.span>
              </span>
            </motion.button>
          </div>

          {/* Stats */}
          <div className="grid grid-cols-3 gap-6 pt-8">
            <div className="text-center">
              <div className="text-2xl font-bold text-cyan-400">10K+</div>
              <div className="text-blue-200 text-sm">Active Users</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-purple-400">$50K+</div>
              <div className="text-blue-200 text-sm">Money Saved</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-400">100+</div>
              <div className="text-blue-200 text-sm">Restaurants</div>
            </div>
          </div>
        </motion.div>

        {/* Right Visual */}
        <motion.div 
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.3, ease: "easeOut" }}
          className="relative"
        >
          <div className="relative">
            {/* Main Image Container */}
            <div className="relative bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-sm rounded-3xl p-8 border border-white/20">
              <img 
                src="/images/hero-section-image.jpg" 
                alt="People sharing food together" 
                className="w-full h-96 lg:h-[500px] object-cover rounded-2xl shadow-2xl"
              />

              {/* Floating Elements */}
              <motion.div 
                animate={{ 
                  y: [0, -10, 0],
                  rotate: [0, 5, 0]
                }}
                transition={{ 
                  duration: 4, 
                  repeat: Infinity, 
                  ease: "easeInOut" 
                }}
                className="absolute -top-4 -right-4 bg-gradient-to-r from-green-400 to-blue-500 text-white px-4 py-2 rounded-full shadow-lg"
              >
                <span className="text-sm font-bold">Save 40%</span>
              </motion.div>

              <motion.div 
                animate={{ 
                  y: [0, 10, 0],
                  rotate: [0, -5, 0]
                }}
                transition={{ 
                  duration: 5, 
                  repeat: Infinity, 
                  ease: "easeInOut",
                  delay: 1
                }}
                className="absolute -bottom-4 -left-4 bg-gradient-to-r from-purple-400 to-pink-500 text-white px-4 py-2 rounded-full shadow-lg"
              >
                <span className="text-sm font-bold">Group Order</span>
              </motion.div>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  </section>
);

// How It Works Section Component
const HowItWorksSection = () => (
  <section id="how-it-works" className="py-20 bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-hidden">
    {/* Background Pattern */}
    <div className="absolute inset-0 bg-[url('data:image/svg+xml,%3Csvg%20width%3D%2260%22%20height%3D%2260%22%20viewBox%3D%220%200%2060%2060%22%20xmlns%3D%22http%3A//www.w3.org/2000/svg%22%3E%3Cg%20fill%3D%22none%22%20fill-rule%3D%22evenodd%22%3E%3Cg%20fill%3D%22%23ffffff%22%20fill-opacity%3D%220.03%22%3E%3Ccircle%20cx%3D%2230%22%20cy%3D%2230%22%20r%3D%222%22/%3E%3C/g%3E%3C/g%3E%3C/svg%3E')] opacity-50"></div>

    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
      <div className="text-center mb-16">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-cyan-500/20 to-blue-500/20 backdrop-blur-sm rounded-full border border-cyan-500/30 mb-6"
        >
          <span className="text-cyan-400 font-semibold"> Simple 3-Step Process</span>
        </motion.div>

        <h2 className="text-5xl font-black text-white mb-6">
          How It{' '}
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-400">
            Works
          </span>
        </h2>
        <p className="text-xl text-cyan-100 max-w-3xl mx-auto leading-relaxed">
          Get your favorite food without the full price tag. It's simple, fun, and saves everyone money!
        </p>
      </div>

      <motion.div 
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.3 }}
        variants={{
          hidden: { opacity: 0 },
          visible: {
            opacity: 1,
            transition: {
              staggerChildren: 0.2
            }
          }
        }}
        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 lg:gap-8"
      >
        {/* Step 1 */}
        <motion.div 
          variants={{
            hidden: { opacity: 0, y: 50 },
            visible: { opacity: 1, y: 0 }
          }}
          transition={{ duration: 0.6, ease: "easeOut" }}
          className="text-center group relative"
        >
          <div className="absolute inset-0 bg-gradient-to-br from-cyan-500/20 to-blue-500/20 rounded-3xl blur-xl group-hover:blur-2xl transition-all duration-500"></div>
          <div className="relative bg-gradient-to-br from-slate-800/80 to-slate-900/80 backdrop-blur-sm rounded-3xl p-8 border border-cyan-500/20 shadow-2xl hover:shadow-cyan-500/25 transition-all duration-500 hover:-translate-y-3">
            <div className="mb-6 relative">
              <motion.div
                whileHover={{ scale: 1.05, rotate: 2 }}
                className="relative overflow-hidden rounded-2xl"
              >
                <img 
                  src="/images/fried-chickenn.jpg" 
                  alt="Fried chicken bucket" 
                  className="w-full h-48 sm:h-56 lg:h-64 object-cover transition-all duration-500"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
              </motion.div>

              {/* Step Number Badge */}
              <div className="absolute -top-4 -left-4 w-16 h-16 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-full flex items-center justify-center shadow-lg border-4 border-slate-800">
                <span className="text-white font-black text-2xl">1</span>
              </div>
            </div>

            <h3 className="text-2xl sm:text-3xl font-bold text-white mb-4 group-hover:text-cyan-400 transition-colors">
              Browse Available Orders
            </h3>
            <p className="text-cyan-100 leading-relaxed text-base">
              See what group orders are available in your area. From pizza to fried chicken, there's always something delicious to join.
            </p>

            {/* Hover Effect Line */}
            <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-0 h-1 bg-gradient-to-r from-cyan-400 to-blue-500 group-hover:w-full transition-all duration-500 rounded-full"></div>
          </div>
        </motion.div>

        {/* Step 2 */}
        <motion.div 
          variants={{
            hidden: { opacity: 0, y: 50 },
            visible: { opacity: 1, y: 0 }
          }}
          transition={{ duration: 0.6, ease: "easeOut" }}
          className="text-center group relative"
        >
          <div className="absolute inset-0 bg-gradient-to-br from-purple-500/20 to-pink-500/20 rounded-3xl blur-xl group-hover:blur-2xl transition-all duration-500"></div>
          <div className="relative bg-gradient-to-br from-slate-800/80 to-slate-900/80 backdrop-blur-sm rounded-3xl p-8 border border-purple-500/20 shadow-2xl hover:shadow-purple-500/25 transition-all duration-500 hover:-translate-y-3">
            <div className="mb-6 relative">
              <motion.div
                whileHover={{ scale: 1.05, rotate: -2 }}
                className="relative overflow-hidden rounded-2xl"
              >
                <img 
                  src="/images/burger.jpg" 
                  alt="Delicious burger" 
                  className="w-full h-48 sm:h-56 lg:h-64 object-cover transition-all duration-500"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
              </motion.div>

              {/* Step Number Badge */}
              <div className="absolute -top-4 -left-4 w-16 h-16 bg-gradient-to-r from-purple-500 to-pink-600 rounded-full flex items-center justify-center shadow-lg border-4 border-slate-800">
                <span className="text-white font-black text-2xl">2</span>
              </div>
            </div>

            <h3 className="text-2xl sm:text-3xl font-bold text-white mb-4 group-hover:text-purple-400 transition-colors">
              Select Your Portion
            </h3>
            <p className="text-purple-100 leading-relaxed text-base">
              Choose exactly how much you want and join the order. Pay only for your portion, not the whole meal.
            </p>

            {/* Hover Effect Line */}
            <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-0 h-1 bg-gradient-to-r from-purple-400 to-pink-500 group-hover:w-full transition-all duration-500 rounded-full"></div>
          </div>
        </motion.div>

        {/* Step 3 */}
        <motion.div 
          variants={{
            hidden: { opacity: 0, y: 50 },
            visible: { opacity: 1, y: 0 }
          }}
          transition={{ duration: 0.6, ease: "easeOut" }}
          className="text-center group relative"
        >
          <div className="absolute inset-0 bg-gradient-to-br from-green-500/20 to-emerald-500/20 rounded-3xl blur-xl group-hover:blur-2xl transition-all duration-500"></div>
          <div className="relative bg-gradient-to-br from-slate-800/80 to-slate-900/80 backdrop-blur-sm rounded-3xl p-8 border border-green-500/20 shadow-2xl hover:shadow-green-500/25 transition-all duration-500 hover:-translate-y-3">
            <div className="mb-6 relative">
              <motion.div
                whileHover={{ scale: 1.05, rotate: 1 }}
                className="relative overflow-hidden rounded-2xl"
              >
                <img 
                  src="/images/pizza.jpg" 
                  alt="Pizza slices" 
                  className="w-full h-48 sm:h-56 lg:h-64 object-cover transition-all duration-500"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
              </motion.div>

              {/* Step Number Badge */}
              <div className="absolute -top-4 -left-4 w-16 h-16 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full flex items-center justify-center shadow-lg border-4 border-slate-800">
                <span className="text-white font-black text-2xl">3</span>
              </div>
            </div>

            <h3 className="text-2xl sm:text-3xl font-bold text-white mb-4 group-hover:text-green-400 transition-colors">
              Split the Bill & Enjoy
            </h3>
            <p className="text-green-100 leading-relaxed text-base">
              When the order is complete, everyone gets their share. Enjoy great food at a fraction of the cost!
            </p>

            {/* Hover Effect Line */}
            <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-0 h-1 bg-gradient-to-r from-green-400 to-emerald-500 group-hover:w-full transition-all duration-500 rounded-full"></div>
          </div>
        </motion.div>
      </motion.div>
    </div>
  </section>
);

// Top Restaurants Section Component
const TopRestaurantsSection = () => (
  <section className="py-16 bg-gradient-to-br from-slate-50 via-cyan-50 to-blue-50">
