import React, { useState } from 'react';
import { motion } from 'framer-motion';
import SignupPage from './components/SignupPage';
import LoginPage from './components/LoginPage';
import LogoutPage from './components/LogoutPage';
import CompleteProfilePage from './components/CompleteProfilePage';
import DeleteAccountPage from './components/DeleteAccountPage';
import UserProfilePage from './components/UserProfilePage';

// Helper function to check if user is logged in
const isLoggedIn = () => {
  return localStorage.getItem('access_token') !== null;
};

// Navigation component
const Navigation = ({ setCurrentPage }) => {
  return (
    <nav className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <div className="flex items-center">
            <h1 className="text-2xl font-bold text-red-600">Gawa</h1>
            <div className="ml-8">
              <select className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-red-500 focus:border-red-500 px-3 py-2">
                <option>Amborkhana, Sylhet</option>
              </select>
            </div>
          </div>

          {/* Navigation Links */}
          <div className="hidden md:flex items-center space-x-8">
            <a href="#" className="text-gray-700 hover:text-red-600 transition-colors">Home</a>
            <a href="#" className="text-gray-700 hover:text-red-600 transition-colors">Explore Foods</a>
            <a href="#" className="text-gray-700 hover:text-red-600 transition-colors">Restaurants</a>
          </div>

          {/* User Actions */}
          <div className="flex items-center space-x-4">
            <button className="p-2 text-gray-600 hover:text-red-600 transition-colors">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
              </svg>
            </button>
            
            {!isLoggedIn() ? (
              <>
                <button 
                  onClick={() => setCurrentPage('login')}
                  className="bg-orange-500 text-white px-6 py-2 rounded-lg hover:bg-orange-600 transition-colors"
                >
                  Log In
                </button>
                <button 
                  onClick={() => setCurrentPage('signup')}
                  className="bg-red-600 text-white px-6 py-2 rounded-lg hover:bg-red-700 transition-colors"
                >
                  Sign Up
                </button>
              </>
            ) : (
              <>
                <button 
                  onClick={() => setCurrentPage('user-profile')}
                  className="text-gray-700 hover:text-red-600 transition-colors"
                >
                  View Profile
                </button>
                <button 
                  onClick={() => setCurrentPage('complete-profile')}
                  className="text-gray-700 hover:text-red-600 transition-colors"
                >
                  Complete Profile
                </button>
                <button 
                  onClick={() => setCurrentPage('delete-account')}
                  className="text-gray-700 hover:text-red-600 transition-colors"
                >
                  Delete Account
                </button>
                <button 
                  onClick={() => setCurrentPage('logout')}
                  className="bg-red-600 text-white px-6 py-2 rounded-lg hover:bg-red-700 transition-colors"
                >
                  Logout
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

// Landing Page Component
const LandingPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilter, setActiveFilter] = useState('All');

  const foodCategories = [
    { name: 'Pizza', image: '/images/pizza.jpg', count: 12 },
    { name: 'Broast', image: '/images/fried-chickenn.jpg', count: 12 },
    { name: 'Chicken', image: '/images/fried-chickenn.jpg', count: 14 },
    { name: 'Burger', image: '/images/burger.jpg', count: 15 }
  ];

  const restaurants = [
    { name: 'The Italian Kitchen', image: '/images/pizza.jpg', rating: 4.9, hours: '12:01 am - 11:59 pm' },
    { name: 'Burger Blast', image: '/images/burger.jpg', rating: 4.8, hours: '12:01 am - 11:59 pm' },
    { name: 'Sushi World', image: '/images/fried-chickenn.jpg', rating: 4.8, hours: '12:01 am - 11:59 pm' },
    { name: 'Taco Town', image: '/images/pizza.jpg', rating: 4.7, hours: '12:01 am - 11:59 pm' },
    { name: 'Green Bowl', image: '/images/burger.jpg', rating: 4.8, hours: '12:01 am - 11:59 pm' },
    { name: 'Spice Street', image: '/images/pizza.jpg', rating: 4.7, hours: '12:01 am - 11:59 pm' }
  ];

  const deals = [
    { name: 'Pizza Palace', image: '/images/pizza.jpg', time: '18 Mins', originalPrice: 24.99, discountPrice: 21.99, description: 'Get 2 large pizzas for the price of 1!' },
    { name: 'Burger Boss', image: '/images/burger.jpg', time: '20 Mins', originalPrice: 24.99, discountPrice: 21.99, description: 'Double cheeseburger meal with fries and drink' },
    { name: 'Noodle Nation', image: '/images/fried-chickenn.jpg', time: '20 Mins', originalPrice: 24.99, discountPrice: 21.99, description: 'Family noodle pack - enough for 4 people' },
    { name: 'Curry Club', image: '/images/pizza.jpg', time: '25 Mins', originalPrice: 24.99, discountPrice: 21.99, description: 'Butter chicken with naan and a dessert included' },
    { name: 'Sushi Zen', image: '/images/burger.jpg', time: '30 Mins', originalPrice: 24.99, discountPrice: 21.99, description: '20-piece sushi platter with miso soup' },
    { name: 'Sweet Tooth Café', image: '/images/pizza.jpg', time: '35 Mins', originalPrice: 24.99, discountPrice: 21.99, description: 'Buy one dessert, get one free!' }
  ];

  const topRestaurants = [
    { name: 'Arby\'s', image: '/images/top-restaurants/burger-king.jpeg' },
    { name: 'Bone\'s', image: '/images/top-restaurants/KFC.jpeg' },
    { name: 'Burger King', image: '/images/top-restaurants/burger-king.jpeg' },
    { name: 'Krispy Kreme', image: '/images/top-restaurants/KFC.jpeg' },
    { name: 'Pizza Hut', image: '/images/top-restaurants/pizza-hut.jpg' },
    { name: 'Subway', image: '/images/top-restaurants/subway.jpg' }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <section className="bg-white py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <motion.div
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
            >
              <h1 className="text-5xl lg:text-6xl font-bold text-gray-900 mb-6">
                Hungry? Let's Deliver Happiness to Your{' '}
                <span className="text-red-600">Doorstep!</span>
              </h1>
              <p className="text-xl text-gray-600 mb-8">
                Explore a world of flavors from the best restaurants in your city. Whether you're craving something new or your favorite comfort food, we've got you covered with quick and easy delivery.
              </p>
              
              {/* Search Bar */}
              <div className="relative mb-6">
                <input
                  type="text"
                  placeholder="Search for food..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full px-6 py-4 pl-12 text-lg border border-gray-300 rounded-xl focus:ring-2 focus:ring-red-500 focus:border-transparent"
                />
                <svg className="absolute left-4 top-1/2 transform -translate-y-1/2 w-6 h-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
              
              {/* Search Suggestions */}
              <div className="flex flex-wrap gap-2">
                <span className="text-sm text-gray-500">Popular:</span>
                {['Pizza', 'Burger', 'Chicken', 'Sushi'].map((item) => (
                  <button
                    key={item}
                    onClick={() => setSearchQuery(item)}
                    className="px-3 py-1 bg-gray-100 text-gray-700 rounded-full text-sm hover:bg-red-100 hover:text-red-600 transition-colors"
                  >
                    {item}
                  </button>
                ))}
              </div>
            </motion.div>
            
            <motion.div
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="relative"
            >
              <img
                src="/images/hero-section-image.jpg"
                alt="Delicious food"
                className="w-full h-96 object-cover rounded-2xl shadow-2xl"
              />
            </motion.div>
          </div>
        </div>
      </section>

      {/* Nearby Restaurants */}
      <section className="py-12 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className="text-3xl font-bold text-gray-900 mb-8">Nearby Restaurants</h2>
          <div className="flex space-x-6 overflow-x-auto pb-4">
            {topRestaurants.map((restaurant, index) => (
              <motion.div
                key={restaurant.name}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className="flex-shrink-0"
              >
                <div className="w-20 h-20 rounded-full overflow-hidden border-2 border-gray-200 hover:border-red-500 transition-colors">
                  <img
                    src={restaurant.image}
                    alt={restaurant.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                <p className="text-center text-sm text-gray-600 mt-2">{restaurant.name}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Top Food Categories */}
      <section className="py-16 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center mb-8">
            <h2 className="text-3xl font-bold text-gray-900">Top Food Categories</h2>
            <div className="flex space-x-2">
              {['All', 'Healthy', 'Italian', 'Asian', 'Mexican'].map((filter) => (
                <button
                  key={filter}
                  onClick={() => setActiveFilter(filter)}
                  className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                    activeFilter === filter
                      ? 'bg-red-600 text-white'
                      : 'bg-white text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  {filter}
                </button>
              ))}
            </div>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {foodCategories.map((category, index) => (
              <motion.div
                key={category.name}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow cursor-pointer"
              >
                <div className="h-48 overflow-hidden">
                  <img
                    src={category.image}
                    alt={category.name}
                    className="w-full h-full object-cover hover:scale-110 transition-transform duration-300"
                  />
                </div>
                <div className="p-4">
                  <h3 className="text-lg font-semibold text-gray-900 mb-1">{category.name}</h3>
                  <p className="text-sm text-gray-600">{category.count} Restaurants</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Popular Restaurants */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center mb-8">
            <h2 className="text-3xl font-bold text-gray-900">Popular Restaurants</h2>
            <div className="flex space-x-2">
              {['All', 'Fast Food', 'Vegetarian', 'Family Dining', 'Dessert Shops'].map((filter) => (
                <button
                  key={filter}
                  onClick={() => setActiveFilter(filter)}
                  className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                    activeFilter === filter
                      ? 'bg-red-600 text-white'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  {filter}
                </button>
              ))}
            </div>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {restaurants.map((restaurant, index) => (
              <motion.div
                key={restaurant.name}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow cursor-pointer"
              >
                <div className="h-48 overflow-hidden">
                  <img
                    src={restaurant.image}
                    alt={restaurant.name}
                    className="w-full h-full object-cover hover:scale-110 transition-transform duration-300"
                  />
                </div>
                <div className="p-4">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">{restaurant.name}</h3>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center">
                      <span className="text-yellow-500">★</span>
                      <span className="ml-1 text-sm text-gray-600">{restaurant.rating}</span>
                    </div>
                    <span className="text-sm text-gray-500">{restaurant.hours}</span>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
          
          <div className="text-center mt-8">
            <button className="bg-red-600 text-white px-8 py-3 rounded-lg hover:bg-red-700 transition-colors">
              View All Restaurants
            </button>
            <p className="text-gray-600 mt-4">
              Dine from the best! Explore top-rated restaurants loved by foodies near you.
            </p>
          </div>
        </div>
      </section>

      {/* Deals Section */}
      <section className="py-16 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className="text-3xl font-bold text-gray-900 mb-8 text-center">
            Super Delicious Deals Just for You!
          </h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {deals.map((deal, index) => (
              <motion.div
                key={deal.name}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow"
              >
                <div className="h-48 overflow-hidden">
                  <img
                    src={deal.image}
                    alt={deal.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div className="p-4">
                  <div className="flex justify-between items-start mb-2">
                    <h3 className="text-lg font-semibold text-gray-900">{deal.name}</h3>
                    <span className="text-sm text-gray-500">{deal.time}</span>
                  </div>
                  <div className="flex items-center mb-2">
                    <span className="text-lg font-bold text-red-600">${deal.discountPrice}</span>
                    <span className="text-sm text-gray-500 line-through ml-2">${deal.originalPrice}</span>
                  </div>
                  <p className="text-sm text-gray-600">{deal.description}</p>
                </div>
              </motion.div>
            ))}
          </div>
          
          <div className="text-center mt-8">
            <button className="bg-red-600 text-white px-8 py-3 rounded-lg hover:bg-red-700 transition-colors">
              See More Deals
            </button>
            <p className="text-gray-600 mt-4">
              Get the best meals at unbeatable prices. These deals are too good to miss!
            </p>
          </div>
        </div>
      </section>

      {/* Special Offers Banner */}
      <section className="py-16 bg-gradient-to-r from-orange-400 to-pink-500">
