    </div>
  );
};

// Main App Component
const App = () => {
  const [currentPage, setCurrentPage] = useState('home');

  const renderPage = () => {
    switch (currentPage) {
      case 'signup':
        return <SignupPage setCurrentPage={setCurrentPage} />;
      case 'login':
        return <LoginPage setCurrentPage={setCurrentPage} />;
      case 'logout':
        return <LogoutPage setCurrentPage={setCurrentPage} />;
      case 'complete-profile':
        return <CompleteProfilePage setCurrentPage={setCurrentPage} />;
      case 'delete-account':
        return <DeleteAccountPage setCurrentPage={setCurrentPage} />;
      case 'user-profile':
        return <UserProfilePage setCurrentPage={setCurrentPage} />;
      default:
        return <LandingPage />;
    }
  };

  return (
    <div className="App">
      {currentPage === 'home' && <Navigation setCurrentPage={setCurrentPage} />}
      {renderPage()}
    </div>
  );
};

export default App;
