  coordinates: { lat: -1.3182, lng: 36.8172 },
  description: 'The ultimate meat lover\'s paradise serving exotic meats from around the world',
  founded: '1980',
  branches: '1',
  specialties: ['Exotic Meats', 'BBQ', 'Grilled Meats', 'African Cuisine'],
  rating: 4.6,
  deliveryTime: '30-45 min'
};

// Meat type endpoints from Spoonacular
const MEAT_ENDPOINTS = {
  chicken: 'https://api.spoonacular.com/recipes/complexSearch?query=chicken&number=10&apiKey=0264506281e5448c81a956ff1f4f1d82',
