export const carnivoreAPI = {
  // Get full menu with real-time data
  async getFullMenu() {
    try {
      // Try to get real meat data first from Spoonacular
      const realMenu = await this.getRealMeatMenu();
      if (realMenu && realMenu.length > 0) {
        console.log('‚úÖ Real meat data loaded successfully from Spoonacular');
        return {
          restaurant: CARNIVORE_CONFIG,
          menu: realMenu,
          meatTypes: Object.keys(MEAT_ENDPOINTS),
          promotions: this.getPromotions(),
          deals: this.getDeals(),
          rewards: this.getRewards(),
          customization: this.getCustomization(),
          locations: this.getLocations()
        };
      }
      
      // Fallback to enhanced mock data
      console.log('‚ö†Ô∏è Using enhanced mock data as fallback');
      return this.getEnhancedMockMenu();
    } catch (error) {
      console.error('Error fetching Carnivore menu:', error);
      return this.getEnhancedMockMenu();
    }
  },

