  async getRealMeatMenu() {
    try {
      console.log('ü•© Fetching from multiple Spoonacular Meat APIs...');
      
      const transformedMenu = [];
      let hasData = false;
      
      // Fetch from all meat type endpoints
      for (const [meatType, endpoint] of Object.entries(MEAT_ENDPOINTS)) {
        try {
          console.log(`üçñ Fetching ${meatType} from:`, endpoint);
          
          const response = await fetch(endpoint);
          console.log(`üì° ${meatType} API Response status:`, response.status);
          console.log(`üì° ${meatType} API Response headers:`, response.headers);
          
          if (!response.ok) {
            console.warn(`‚ö†Ô∏è ${meatType} API request failed with status:`, response.status);
            const errorText = await response.text();
            console.warn(`‚ö†Ô∏è ${meatType} API error response:`, errorText);
            continue;
          }
          
          const data = await response.json();
          console.log(`‚úÖ Successfully fetched ${meatType} data:`, data);
